private List getListCondition(javax.servlet.http.HttpServletRequest request, StudentRzdState pv, boolean isAll)
{
    UserInfo userInfo = Tool.getUserInfo(request);
    int shownum = ParamUtils.getIntParameter(request, "shownum", userInfo.getDispNum());
    String orderfield = ParamUtils.getParameter(request, "orderfield", "Id");
    String ordertype = ParamUtils.getParameter(request, "ordertype", "desc");
    List cdt = new ArrayList();
    String qval = "";
    //学院用户只显示本学院学生
    if(userInfo.hasFunPower(Power.UserAdminPower)){
    	int deptid=userInfo.getDeptId();
    	cdt.add("printid="+deptid);
    }
    //_academy_
    String _academy_=ParamUtils.getParameter(request, "_academy_","");
    if(!"".equals(_academy_)){
    	cdt.add("printid="+_academy_);
    }
    
    
    //printtime
    String printtime=ParamUtils.getParameter(request, "_printtime_", "");
    if(!"".equals(printtime)){
    	cdt.add("printtime like '%"+printtime+"%'");
    }
    //stuid
    StudentRzdUtil srUtil=new StudentRzdUtil();
    String _sno_=ParamUtils.getParameter(request, "_stuNo_","");
    if(!"".equals(_sno_)){
		int userid=srUtil.getUseridByStuNo(_sno_);    	
    	cdt.add("stuid="+userid);
    }
    
    
    
    List QueryValues = new ArrayList();
    if (!isAll) {
        int currpage = ParamUtils.getIntParameter(request, "page", 1);
        com.xietong.software.common.PageControl pc = new com.xietong.software.common.PageControl(pv.getCount(cdt), currpage, shownum);
        cdt.add("limit " + pc.getOffset() + "," + pc.getShownum());
        request.setAttribute("PageControl", pc);
    }
    String cname = pv.getFieldByProperty(orderfield);
    if (cname.length() != 0) {
        cdt.add("order by " + cname + " " + ordertype);
        log.debug("order by " + cname + " " + ordertype);
    }
    request.setAttribute("queryfields", QueryFields);
    request.setAttribute("queryvalues", QueryValues);
    return cdt;
}
